import base64

def img2base64(filepath):
    f=open(filepath,'rb')
    ls_f=base64.b64encode(f.read())
    f.close()
    return  ls_f

def base642img(base64str,filepath):
    #base64str=base64str.replace('data:image/jpg;base64,','')
    imgdata=base64.b64decode(base64str)
    file=open(filepath,'wb')
    file.write(imgdata)
    file.close()



